    protected int fillPreparedStatement(PreparedStatement ps, ContentBean bean, int searchType) throws DAOException
    {
        if (bean == null) {
            return 0;
        }
        int _dirtyCount = 0;
        try
        {
            if (bean.isMenuNumberModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getMenuNumber() + "]");
                if (bean.getMenuNumber() == null) { ps.setNull(++_dirtyCount, Types.TINYINT); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getMenuNumber()); }
            }
            if (bean.isSynchronizationTimestampModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getSynchronizationTimestamp() + "]");
                if (bean.getSynchronizationTimestamp() == null) { ps.setNull(++_dirtyCount, Types.TIMESTAMP); } else { ps.setTimestamp(++_dirtyCount, new java.sql.Timestamp(bean.getSynchronizationTimestamp().getTime())); }
            }
            if (bean.isStatusModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getStatus() + "]");
                if (bean.getStatus() == null) { ps.setNull(++_dirtyCount, Types.TINYINT); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getStatus()); }
            }
            if (bean.isFilenameModified()) {
                switch (searchType) {
                    case SEARCH_EXACT:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getFilename() + "]");
                        if (bean.getFilename() == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getFilename()); }
                        break;
                    case SEARCH_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getFilename() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getFilename() + "%");
                        break;
                    case SEARCH_STARTING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [" + bean.getFilename() + "%]");
                        ps.setString(++_dirtyCount, "%" + bean.getFilename());
                        break;
                    case SEARCH_ENDING_LIKE:
                        // System.out.println("Setting for " + _dirtyCount + " [%" + bean.getFilename() + "]");
                        if (bean.getFilename() + "%" == null) { ps.setNull(++_dirtyCount, Types.VARCHAR); } else { ps.setString(++_dirtyCount, bean.getFilename() + "%"); }
                        break;
                    default:
                        throw new DAOException("Unknown search type " + searchType);
                }
            }
            if (bean.isCreationTimestampModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCreationTimestamp() + "]");
                if (bean.getCreationTimestamp() == null) { ps.setNull(++_dirtyCount, Types.TIMESTAMP); } else { ps.setTimestamp(++_dirtyCount, new java.sql.Timestamp(bean.getCreationTimestamp().getTime())); }
            }
            if (bean.isCategoryIdModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getCategoryId() + "]");
                if (bean.getCategoryId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getCategoryId()); }
            }
            if (bean.isContentIdModified()) {
                // System.out.println("Setting for " + _dirtyCount + " [" + bean.getContentId() + "]");
                if (bean.getContentId() == null) { ps.setNull(++_dirtyCount, Types.INTEGER); } else { Manager.setInteger(ps, ++_dirtyCount, bean.getContentId()); }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        return _dirtyCount;
    }


